import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import SideBar from "./components/sidebar/SideBar";
import NavBar from "./components/pages/Navbar";

import Content from "./components/content/Content";
import MultiRangeSlider from "./components/content/pages/RangeSlider";

class App extends React.Component {
  state = {
    isGovernmentSelected: false
  };

  handleGovernmentSelection = (selected) => {
    this.setState({ isGovernmentSelected: selected });
  };
  render() {
    return (
      <div>
        <NavBar isGovernmentSelected={this.state.isGovernmentSelected} />
        <SideBar onGovernmentSelect={this.handleGovernmentSelection} />
        {/* Rest of your components */}
      </div>
    );
  }
  constructor(props) {
    super(props);

    // Moblie first
    this.state = {
      isOpen: false,
      isMobile: true
    };

    this.previousWidth = -1;
  }

  updateWidth() {
    const width = window.innerWidth;
    const widthLimit = 576;
    const isMobile = width <= widthLimit;
    const wasMobile = this.previousWidth <= widthLimit;

    if (isMobile !== wasMobile) {
      this.setState({
        isOpen: !isMobile
      });
    }

    this.previousWidth = width;
  }

  /**
   * Add event listener
   */
  componentDidMount() {
    this.updateWidth();
    window.addEventListener("resize", this.updateWidth.bind(this));
  }

  /**
   * Remove event listener
   */
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateWidth.bind(this));
  }

  toggle = () => {
    this.setState({ isOpen: true });
  };

  render() {
    return (
      <div className="App wrapper">
        <SideBar toggle={this.toggle} isOpen={this.state.isOpen} />
        <Content toggle={this.toggle} isOpen={this.state.isOpen} />

      </div>


    );
  }
}

export default App;
